  // show table captions
  var tabs = document.getElementsByTagName("article")[0].getElementsByTagName("table");
  for (var i=0; i < tabs.length; i++) {
    if (tabs[i].hasAttribute("caption")) {
      var cap = tabs[i].createCaption();
      cap.innerHTML = tabs[i].getAttribute("caption");
    }
  }
  // add codeblock class to examples
  var pres = document.getElementsByTagName("article")[0].getElementsByTagName("pre");
